import React,{useState} from 'react';
import './Box.css'

export default function Box(props){

    const [defualtColor, setDefualtColor] = useState(props.color);
    const [color, setColor] = useState(0);
    let className = 'box';
    className += ' ' + defualtColor;

    function changeColor(){
        if (defualtColor === 'white'){
            setDefualtColor('black');
            //props.isBoxWhite(false);
        } else {
            setDefualtColor('white')
            //props.isBoxWhite(true);
        }
    }

    return (
        <div className={className} onClick={() => changeColor()}>

        </div>

    )

}
