import React,{useState} from 'react';
import './Box.css'
import { shallowEqual, useDispatch, useSelector } from 'react-redux';


export default function Box(props){

    const [defualtColor, setDefualtColor] = useState(props.color);
    const [number, setNumber] = useState(1);
    const dispatch = useDispatch();
    let className = 'box';
    className += ' ' + defualtColor;

    function changeColor(){
        if (defualtColor === 'white'){
            setDefualtColor('black');
            setNumber(-1);
        } else {
            setDefualtColor('white')
            setNumber(1);
        }
    }

    function handleOnClick(){
        dispatch({
            type:'ADD',
            value: number,
        })
    }

    return (
        <div className={className} onClick={() => (changeColor(),handleOnClick())}>
        </div>  
    )

}
