import React, { useEffect, useState } from 'react';
import { shallowEqual, useDispatch, useSelector } from 'react-redux';
import Box from './Box';
import './ParentComponent.css';



export default function ParentComponent(){

    const [countNumber, setCountNumber] = useState(0);

    const currentSum = useSelector((state) => {
        return state.sum;
    },shallowEqual);

    const dispatch = useDispatch();

    function handleOnClick(props){
        console.log(props);
        if (props) {
            setCountNumber = 1;
            dispatch({
                type:'ADD',
                value: countNumber,
            })
        }
    }

    return (
        <div>
            <div className='counter'>Count: {currentSum}</div>
            <div className='container'>
                <Box color={'white'} onClick={() => handleOnClick(this.Box)}/>
                <Box color={'white'}/>
                <Box color={'white'}/>
                <Box color={'white'}/>
            </div>
        </div>

    )
}
