    const [wordList, setWordList] = useState(props.array)
    const [boardArray,setBoardArray] = useState(props.boardArray)


    return (
        <div className='AnswerBoard'>
            {boardArray.map((item) => (
                <AnswerBox keyV={item.char} color={item.color} key={uuidv4()}/>
            ))}

            {/* {wordList.map((item) => (
                <AnswerBox keyV={item.char} color={item.color} key={uuidv4()}/>
            ))} */}
        </div>
    );
}
