import React, {useState} from 'react';
import { Component } from 'react';
import './App.css'
import Keyboard from './Keyboard';
import { Navbar, Container, NavDropdown, Nav, Dropdown} from "react-bootstrap";
import { shallowEqual, useSelector } from 'react-redux';


export default function App() {

    const[words, setWords] = useState('');

    const currentWord = useSelector((state) => {
        return state.curr;
    },shallowEqual);


    //setWords(currentWord);

    return (
        <div className='App'>
            <div className="navbar">
                <Navbar bg="light" expand="lg">
                <Container>
                    <Navbar.Brand href="/home">Wordle !!!</Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        <Nav.Link href="/home">Home</Nav.Link>
                        <Nav.Link href="/gameRule">Game Rules</Nav.Link>
                        <Dropdown>
                            <Dropdown.Toggle variant="success" id="dropdown-basic">
                            Games
                            </Dropdown.Toggle>

                            <Dropdown.Menu>
                            <Dropdown.Item href="/app">easy game</Dropdown.Item>
                            <Dropdown.Item href="/app">medium game</Dropdown.Item>
                            <Dropdown.Item href="/app">hard game</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>
                    </Nav>
                    </Navbar.Collapse>
                </Container>
                </Navbar>
            </div>

            {words}
            {/* <input onKeyUp={(event) => this.setState({word:event.target.value})} type="text" placeholder='enter 5 letter words'/>
            {this.state.word}
            <button style={{width: "100px", height: "100px"}} onClick={() => this.setState({word: 123})}/> */}
            <Keyboard className='keyboard'/>
        </div>
    );
}
