import React, { useState } from 'react'
import { shallowEqual, useDispatch, useSelector } from 'react-redux';
import Box from './Box'
import './ParentComponent.css'
import { createStore } from 'redux';
import combineReducers from './reducer/mathReducer';


export default function ParentComponent(){

    const [countNumber, setCountNumber] = useState(0);
    const store = createStore(combineReducers);

    const currentSum = useSelector((state) => {
        return state.sum
     },shallowEqual);

    const dispatch = useDispatch();

    function handleOnClick(props){
        if (!props) {
            setCountNumber = 1;
            dispatch({
                type:'ADD',
                value: countNumber,
            })
        }
    }


    return (
        <div>
            <div className='counter'>Count: {store.getState()}</div>
            <div className='container'>
                <Box color={'white'} isBoxWhite={(props)=>handleOnClick(props)}/>
                <Box color={'white'} isBoxWhite={(props)=>handleOnClick(props)}/>
                <Box color={'white'} isBoxWhite={(props)=>handleOnClick(props)}/>
                <Box color={'white'} isBoxWhite={(props)=>handleOnClick(props)}/>
            </div>
        </div>

    )
}
