import { combineReducers } from "redux";

function addReducer(state = '', action) {
    if (action.type === 'ADD') {
        return state + action.value;
    } else if (action.type === 'RESET'){
        return '';
    }
    return state;
}

function fiveReducer(array = [], action){
    if (action.type === 'FIVE'){
        array = action.value;
        return array;
    }
    return array;
}

export default combineReducers({
    curr: addReducer,
    five: fiveReducer,
})
