import React from 'react';
import './WordDisplayBar.css';


export default function WordDisplayBar(props){

    let name = props.name;
    if (!props.name){
        name = 'Please enter a word';
    }
    const display ='display';

    return (
        <div className={() => (name === 'Please enter a word') ? {name}: {display}}>{name}</div>  
    )

}
