import React,{useState} from 'react';
import './Box.css'
import { shallowEqual, useDispatch, useSelector } from 'react-redux';


export default function Box(props){

    const [defualtColor, setDefualtColor] = useState(props.color);
    const currNumber = props.curr;
    let className = 'box';
    className += ' ' + defualtColor;

    function changeColor(){
        if (defualtColor === 'white'){
            setDefualtColor('black');
            return currNumber;   
        } else {
            setDefualtColor('white')
            return -1;
        }
    }   

    return (
        <div className={className} onClick={() => (props.changeNumber(changeColor()),props.handleOnClick())}>
        </div>  
    )

}
