import { combineReducers } from "redux";

function addReducer(state = '', action) {
    if (action.type === 'ADD') {
        return state + action.value;
    } else if (action.type === 'RESET'){
        return '';
    }
    return state;
}

function fiveReducer(state = [], action){
    if (action.type === 'FIVE'){
        state = action.value;
        return state;
    }
    return state;
}


function wordList(state = [], action){
    if (action.type === 'LIST'){
        state = state.push(action.value)
        return state;
    }
    return state;
}

function pickAWord(state = '', action){

    if (action.type === 'PICK'){
        state = action.value;
        return state;
    }
    return state;
}

export default combineReducers({
    curr: addReducer,
    five: fiveReducer,
    list: wordList,
    pick: pickAWord,
})
