import React,{useState} from 'react';
import './Box.css'
import { shallowEqual, useDispatch, useSelector } from 'react-redux';


export default function Box(props){

    const [defualtColor, setDefualtColor] = useState(props.color);
    const [number, setNumber] = useState(1);

    let className = 'box';
    className += ' ' + defualtColor;

    function changeColor(){
        if (defualtColor === 'white'){
            setDefualtColor('black');
            setNumber(-1);
        } else {
            setDefualtColor('white')
            setNumber(1);
        }
    }


    return (
        <div className={className} onClick={() => (changeColor(),this.props.handleOnClick(number))}>
        </div>  
    )

}
