import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { shallowEqual, useSelector } from 'react-redux';


export default function App() {

    const [input, setInput] = useState(0);

    const currentSum = useSelector((state) => {
        return state.sum
    }, shallowEqual);

    const dispatch = useDispatch();

    function handleOnInput(event) {
        setInput(event.target.value);
    }

    function handleOnClick(id) { 
        if (id === 'add'){
            dispatch({
                type: 'ADD',
                value: input,
            });
        } else if (id === 'sub'){
            dispatch({
                type: 'SUB',
                value: input,
            });
        }
    }

    return (<div>
        <h1>{"Let's Have Fun With Redux"}</h1>
        <h2>{"Use the form below to add to the total"}</h2>
        <h3>{currentSum}</h3>
        <input onInput={(event) => handleOnInput(event)}/>
        <button onClick={({id='add'}) => handleOnClick(id)}>Add</button>
        <button onClick={({id='sub'}) => handleOnClick(id)}>Subtract</button>
    </div>)
}
