import React, {useState} from 'react';
import { Component } from 'react';
import './App.css'
import Keyboard from './Keyboard';
import { Navbar, Container, NavDropdown, Nav, Dropdown, Button} from "react-bootstrap";
import { shallowEqual, useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';


export default function App() {

    const[words, setWords] = useState(['']);
    const dispatch = useDispatch();

    const currentWord = useSelector((state) => {
        return state.curr;
    },shallowEqual);

    function handReset(){

        setWords(words => ({

            currentWord,
        }));

        dispatch(
            {
                type:'RESET',
                value: '',
            }
        )
    }

    return (
        <div className='App'>
            <div className="navbar">
                <Navbar bg="light" expand="lg">
                <Container>
                    <Navbar.Brand href="/home">Wordle !!!</Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        <Nav.Link href="/home">Home</Nav.Link>
                        <Nav.Link href="/gameRule">Game Rules</Nav.Link>
                        <Dropdown>
                            <Dropdown.Toggle variant="success" id="dropdown-basic">
                            Games
                            </Dropdown.Toggle>

                            <Dropdown.Menu>
                            <Dropdown.Item href="/app">easy game</Dropdown.Item>
                            <Dropdown.Item href="/app">medium game</Dropdown.Item>
                            <Dropdown.Item href="/app">hard game</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>
                    </Nav>
                    </Navbar.Collapse>
                </Container>
                </Navbar>
            </div>
            {currentWord}
            {console.log('words: '+ words)}
            {console.log('curr: ' + currentWord)}
            <div className='wholeKeyboard'>
                <Button className='DeleteButton' onClick={() => handReset()} variant="success">Delete</Button>
                <Button className='SubmitButton' onClick={() => handReset()} variant="success">Submit</Button>
                <Keyboard className='keyboard'/>
            </div>
        </div>
    );
}
